{% extends 'base.html.twig' %}

{% block main %}
    <main>
        <div class="py-5 bg-light">
            <div class="container">
                <div class="py-5 text-center">
                    <h1 class="fw-bold">{{ article.title }}</h1>
                    <p class="text-muted">
                    {{
                        if(1 == 1)
                            'dd';
                    }}
                    </p>
                </div>

